    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
        if (jTextField1.getText().equals("") || jTextField2.getText().equals("")) {
        } else {
            dtm.addRow(new Object[]{jTextField1.getText(), jTextField2.getText()});
        }

    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        int linha;
        linha = jTable1.getSelectedRow();
        DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
        dtm.removeRow(linha);
        //ou
        //DefaultTableModel dtm=(DefaultTableModel) jTable1.getModel();
        //dtm.removeRow(jTable1.getSelectedRow);

        int botao;
        botao = JOptionPane.showConfirmDialog(null, "Tem certeza que deseja excluir?", "ConfimaÃ§Ã£o", JOptionPane.YES_NO_OPTION, JOptionPane.WARNING_MESSAGE);
        if (botao == JOptionPane.YES_OPTION) {
            dtm.removeRow(linha);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        int linha;
        linha = jTable1.getSelectedRow();
        jTextField1.setText(jTable1.getValueAt(linha, 0).toString());
        jTextField2.setText(jTable1.getValueAt(linha, 1).toString());
    }//GEN-LAST:event_jTable1MouseClicked

    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        int linha;
        linha = jTable1.getSelectedRow();
        DefaultTableModel dtm = (DefaultTableModel) jTable1.getModel();
        if (jTextField1.getText().equals("") || jTextField2.getText().equals("")) {
        } else {
            dtm.setValueAt(jTextField1.getText(), linha, 0);
            dtm.setValueAt(jTextField2.getText(), linha, 1);
        }


    }//GEN-LAST:event_jButton6ActionPerformed

